import React, {Component} from 'react';
import '../../../Assets/Css/ChoicePage.css';
import choiceAPI from '../../../API/choiceAPI.js'
import Button from 'react-bootstrap/Button'

class ChoicePage extends Component {

	constructor(){
		super();
		this.state = {
			listImages : [],
			currentImg : "",
			currentDesc : "",
			indexCat : 0,
			indexImg : 0
		}
		this.api = new choiceAPI();
		//this.setState({listImages : this.api.getCategory()})
		this.state.listImages = this.api.getCategory();
		this.state.currentImg = "Images/deplacements/" + this.state.listImages[this.state.indexCat].deplacements[this.state.indexImg].name + ".jpg";
		this.state.currentDesc = this.state.listImages[this.state.indexCat].deplacements[this.state.indexImg].desc;
		//console.log(this.state.currentImg)
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick(){
		const nbImages = {deplacements : 5, habitation : 7, loisirs : 4, nutrition : 7, relationscom : 3,
			responsabilites : 1, soinspersonnels : 17}
		let tempImg = this.state.indexImg + 1;
		let tempCat = 0;
		console.log(tempImg, nbImages.deplacements)
		if (this.state.indexCat == 0){
			if (tempImg >= nbImages.deplacements){
				this.state.indexCat = tempCat + 1;
			}
		}

		this.state.indexImg = temp;
		this.setState({currentImg : 
			"Images/deplacements/" + this.state.listImages[this.state.indexCat].deplacements[this.state.indexImg].name + ".jpg"
		})
	}

	render(){
		return(
		<div className="page">
			<div className="mb-5">
				<p>{this.state.currentDesc}</p>
				<img src={this.state.currentImg} alt="habit" className="imgHabit"/>
			</div>
			<div className="row d-flex justify-content-center divHeigt">
				<Button className="like" onClick={this.handleClick}>
					<div className="page"> J'aime </div>
					<input className="imgButton" type="image" src="Images/like.png" alt="j'aime"/>
				</Button>
				<div className="col-sm-1"> </div>
				<Button className="dislike" onClick={this.handleClick}>
					<div className="page"> J'aime pas</div>
					<input className="imgButton" type="image" src="Images/dislike.png" alt="je n'aime pas"/>
				</Button>
			</div>
		</div>
		);
	}
};

export default ChoicePage;
