//Login endpoint

import axios from "axios";

class LoginAPI{

    //test with await if doesn't work
    async checkLogin(login, password){ 
        // Uncomment to link with backend
        let res = await axios.post('/api/v1/users/login',{
            user: {
                email : login,
                pwd : password 
            }
        })
        .catch(error => console.log(error))
        return res;
    }

