import React, { Component } from 'react';
import SortingTopicsAPI from '../../../API/SortingTopicsAPI.js'

class SortingTopics extends Component {

    constructor(){
        super();
        this.state = {
            categories : [],
            listImages : [],
            indexCat : 0,
            indexImg : 0
        }
		this.api = new SortingTopicsAPI();
        this.handleClick = this.handleClick.bind(this);
        this.listImagesByCat = this.listImagesByCat.bind(this);
    }

    componentDidMount(){
        this.listImagesByCat();
    }

    listImagesByCat(){
        //let categories = localStorage.getItem('categories');
        let categories = ["soinspersonnels", "deplacements", "nutrition", "loisirs"]
        let allImages = this.api.getImages();
        let toReturn = []
        let length;
        let currentCat = categories[this.state.indexCat];
        switch(currentCat){
            case "deplacements":
                length = 5
                this.state.indexCat = 0;
                break;
            case "habitation":
                length = 7
                this.state.indexCat = 1;
                break;
            case "loisirs":
                length = 4
                this.state.indexCat = 2;
                break;
            case "nutrition":
                length = 7
                this.state.indexCat = 3;
                break;
            case "relationscom":
                length = 3
                this.state.indexCat = 4;
                break;
            case "responsabilites":
                length = 1
                this.state.indexCat = 5;
                break;
            case "soinspersonnels":
                length = 17
                this.state.indexCat = 6;
                break;
        }
        console.log(length);
        for(let i = 0; i < length; i++){
            let toReturnChildren = [];
            //console.log(allImages[this.state.indexCat][this.state].name)
            console.log(this.state.indexImg);
            let path = "Images/"+currentCat+"/"+allImages[this.state.indexCat][this.state.indexImg].name+".jpg";
            let desc = allImages[this.state.indexCat][this.state.indexImg].desc;
            toReturnChildren.push(<img src={path} alt={desc}/>)
            if(this.state.indexImg <= length){
                this.state.indexImg += 1;
            }
            toReturn.push(<div>{toReturnChildren}</div>)
        }
        this.setState({listImages : toReturn})
        console.log(this.state.listImages);
        return this.state.listImages;
    }

    handleClick(){
        console.log("ok")
    }

    render() {
        return (
            <div>
                {this.listImages}
            </div>
        );
    }
}

export default SortingTopics;
