//Login endpoint

import axios from "axios";

class LoginAPI{

    //test with await if doesn't work
    checkLogin(login, password){
        let resp;
        // Uncomment to link with backend
        axios.post('/api/v1/users/login',{
            user: {
                email : login,
                pwd : password 
            }
        })
        .then(response => resp = response.data)
        .catch(error => console.log(error))
        return resp;
    }

