import React, { Component } from 'react'

import '../../../Assets/Css/ChildSummary.css';
import ChildSummaryAPI from '../../../API/ChildSummaryAPI.js'

import bus from "../../../Assets/Images/deplacements/bus.jpg"

class ChildSummary extends Component{

    constructor(){
        super();
        this.state = {
            listImages : []
        }
        this.api = new ChildSummaryAPI();
    }

    createSummary(){
        //Can't seem to use setState somehow
        //this.setState({listImages : this.api.getChildSummary()});
        let images = this.api.getChildSummary();
        this.state.listImages = images;
        //console.log(this.state.listImages);
        let summary = [];
        let list = this.state.listImages;
        for(let i = 0; i < 3; i++){
            let children = []
            let path = "../../../Assets/Images/deplacements/"+list[i].name+".jpg";
            console.log(path)
            console.log("true :", bus)
            children.push(<img src=""/>)
            if (list[i].like){
                children.push("like")
            } else {
                children.push("not like")
            }
            if (list[i].help){
                children.push("help")
            } else {
                children.push("no help")
            }
            if (list[i].happy){
                children.push("happy")
            } else {
                children.push("not happy")
            }
            summary.push(<div>{children}</div>)
        }
        return summary
    }


    render(){
        return(
            <div>
                <p>hello</p>
                {this.createSummary()}
                <img src="Images/deplacements/bus.jpg"/>
            </div>
        )
    }
}

export default ChildSummary;
