//Login endpoint

import API from "./API.js"
import axios from 'axios'

class LoginAPI extends API{

    constructor(){
        super();
        this.endpoint = this.url + "/login";
    }

    test(){
        return "hey"
    }

    checkLogin(login, password){
        axios.post(
            this.endpoint,
            { login : login},
            { password : password}
        ).then(res => {
            //console.log(res.data);
            return "yes";
        })
    }

}

export default LoginAPI;
