//Login endpoint

import axios from "axios";

class LoginAPI{

    sendResult(res){
        return res;
    }

    //test with await if doesn't work
    checkLogin(login, password){ 
        // Uncomment to link with backend
        axios.post('/api/v1/users/login',{
            user: {
                email : login,
                pwd : password 
            }
        })
        .then(res =>{
            sendResult(res.data)
        })
        .catch(error => console.log(error))
        return res;
    }



