//Login endpoint

import axios from "axios";

class LoginAPI{

    //test with await if doesn't work
    checkLogin(login, password){
        let resp;
        // Uncomment to link with backend
        axios.post('/api/v1/users/login',{
            user: {
                email : login,
                pwd : password 
            }
        })
        .then(response => {
            if (response.data[0].id == ''){
                console.log("yes")
            } else {
                console.log("no")
            }
        })
        .catch(error => console.log(error))
    }

